    private void btnGuardarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnGuardarMouseClicked
        // TODO add your handling code here:
        
        Vehiculo vei = new Vehiculo(txtModelo.getText(), txtPlacas.getText(), cbxSocio.getSelectedItem().toString());
        vei.guardar(vei);
        
        txtModelo.setText("");
        txtPlacas.setText("");
        cbxSocio.setSelectedItem("-Seleccione-");
    }//GEN-LAST:event_btnGuardarMouseClicked

